import { useState } from 'react';
import './App.css';


function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);

  const handleSubmit = function(e){
    setInput("");
    e.preventDefault();
  }

  const handleChange = function(e){
    setInput(e.target.value);
  }

  const addTodo = function(){
    if(input !== ""){
      const newTodos = [...todos, input];
      setTodos(newTodos)
    }
  }

  const removeTodo = function(todo, id){
    console.log(todo.id)
  }
  return (
    <div className="App">
      <h1>Todo App</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder='Write your task here' value={input} onChange={handleChange}></input>
        <button onClick={addTodo}>Add Todo</button>
      </form>

    {/*Todo List*/}

    <div>
      {todos.map((todo) => {
        return <div id={Math.floor(Math.random())} key={Math.floor(Math.random())} style={{display: 'flex'}}>
          <div>{todo}</div>
          <i className="bi bi-trash3" onClick={() => removeTodo(todo, todo.id)}></i>
        </div>
      })}
    </div>
    </div>
  );
}

export default App;
