import { useState } from "react";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([
    {
      id: null,
      value: "",
    },
  ]);
  const [editing, setEditing] = useState(false)
  const [update, setUpdate] = useState([{
    id: null,
    value: '',
  }])

  const handleSubmit = function (e) {
    setInput("");
    e.preventDefault();
  };

  const handleChange = function (e) {
    setInput(e.target.value);
  };

  const addTodo = function () {
    if (input !== "") {
      const newTodo = { id: Math.floor(Math.random() * 100000), value: input };
      const newTodos = [...todos];
      newTodos.push(newTodo);
      setTodos(newTodos);
    }
  };

  const removeTodo = function (id) {
    const removedTodos = todos.filter((todo) => {
      return todo.id !== id;
    });
    setTodos(removedTodos);
  };

  const updateTodo = function (id) {
    setEditing(true)
    const updateTodos = todos.filter(todo => {
      return todo.id !== id
    })

  };
  return (
    <div className="App">
      <h1>Todo App</h1>
      <form onSubmit={handleSubmit} className="mt-5">
        <input
          className="form-control"
          placeholder="Write your task here"
          value={input}
          onChange={handleChange}
        ></input>
        <button className="btn btn-primary" onClick={addTodo}>
          Add Todo
        </button>
      </form>

      {/*Todo List*/}
      {editing ? <form onSubmit={handleSubmit} className="mt-5">
        <input
          className="form-control"
          placeholder="Write your task here"
          value={update}
          onChange={handleChange}
        ></input>
        <button className="btn btn-primary" onClick={() => udpa}>
          Update Todo
        </button>
      </form>:<div
        style={{
          margin: "10px auto",
          display: "flex",
          backgroundColor: "rgb(228,228,228)",
          borderRadius: "5px",
          gap: "10px",
          flexDirection: "column",
          padding: "10px",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {todos.map((todo) => {
          if (todo.value !== "")
            return (
              <div
                key={todo.id}
                style={{
                  display: "flex",
                  backgroundColor: "white",
                  borderRadius: "5px",
                  width: "80%",
                  justifyContent: "space-between",
                  padding: "10px",
                }}
              >
                <div>{todo.value}</div>
                <div>
                  <i
                    className="bi bi-trash3"
                    onClick={() => removeTodo(todo.id)}
                  ></i>
                  <i
                    className="bi bi-pencil-square"
                    onClick={() => updateTodo(todo.id)}
                  ></i>
                </div>
              </div>
            );
        })}
      </div>}


    </div>
  );
}

export default App;
