import { useState } from "react";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);
  const [editInput, setEditInput] = useState("");
  const [editTodo, setEditTodo] = useState();

  const handleSubmit = function (e) {
    setInput("");
    e.preventDefault();
  };

  const handleChange = function (e) {
    setInput(e.target.value);
  };

  const onEditInput = (e) => setEditInput(e.target.value);

  const onEdit = (todoID) => {
    const editTodo = todos.find(todo => todo.id === todoID);
    setEditTodo(editTodo);
  };

  const addTodo = () => {
    if (input !== "") {
      const newTodo = { id: Math.floor(Math.random() * 100000), value: input };
      const newTodos = [...todos, newTodo];
      setTodos(newTodos);
    }
  };

  const updateTodo = () => {
    const updatedTodo = {...editTodo, value: editInput};
    const newTodos = [...todos];
    const editTodoIndex = todos.findIndex(todo => todo.id === updatedTodo.id);
    newTodos.splice(editTodoIndex, 0, updatedTodo);
    console.log(editTodoIndex, newTodos);
    setTodos(newTodos);
  };

  const removeTodo = (id) => {
    const removedTodos = todos.filter((todo) => {
      return todo.id !== id;
    });
    setTodos(removedTodos);
  };



  return (
    <div className="App">
      <h1>Todo App</h1>
      <form onSubmit={handleSubmit} className="mt-5">
        <input
          className="form-control"
          placeholder="Write your task here"
          value={input}
          onChange={handleChange}
        ></input>
        <button className="btn btn-primary" onClick={addTodo}>
          Add Todo
        </button>
      </form>

      {/*Todo List*/}

      {editTodo && <form className="mt-5">
        <input
          className="form-control"
          placeholder="Update your task here"
          value={editInput}
          onChange={onEditInput}

        ></input>
        <button type="button" className="btn btn-primary" onClick={updateTodo}>
          Update Todo
        </button>
      </form>}
      <div
        style={{
          margin: "10px auto",
          display: "flex",
          backgroundColor: "rgb(228,228,228)",
          borderRadius: "5px",
          gap: "10px",
          flexDirection: "column",
          padding: "10px",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {todos.map((todo) => {
          if (todo.value !== "")
            return (
              <div
                key={todo.id}
                style={{
                  display: "flex",
                  backgroundColor: "white",
                  borderRadius: "5px",
                  width: "80%",
                  justifyContent: "space-between",
                  padding: "10px",
                }}
              >
                <div>{todo.value}</div>
                <div>
                  <i
                    className="bi bi-trash3"
                    onClick={() => removeTodo(todo.id)}
                  ></i>
                  <i className="bi bi-pencil-square" onClick={() => {onEdit(todo.id)}}></i>  
                </div>
              </div>
            );
        })}
      </div>

    </div>
  );
}

export default App;
