import { useState } from 'react';
import './App.css';


function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([{
    text : "",
    id: null,
  }]);

  const handleSubmit = function(e){
    setInput("");
    e.preventDefault();
  }

  const handleChange = function(e){
    setInput(e.target.value);
  }

  const addTodo = function(){
    const newTodos = [...todos, {text: input, id: Math.floor(Math.random() * 100000)}];
    setTodos(newTodos)
  }

  const removeTodo = function(todo, id){
    const updatedTodos = todos.filter((id) => {
      return todo.id !== id
    })
    setTodos(updatedTodos)
  }
  return (
    <div className="App">
      <h1>Todo App</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder='Write your task here' value={input} onChange={handleChange}></input>
        <button onClick={addTodo}>Add Todo</button>
      </form>

    {/*Todo List*/}

    <div>
      {todos.map((todo, id) => {
        return <div id={todo.id} style={{display: 'flex'}}>
          <div>{todo.text}</div>
          <i class="bi bi-trash3" onClick={removeTodo}></i>
        </div>
      })}
    </div>
    </div>
  );
}

export default App;
