import { useState } from 'react';
import './App.css';


function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([
    {
      id: null,
      value: "",
    }
  ]);

  const handleSubmit = function(e){
    setInput("");
    e.preventDefault();
  }

  const handleChange = function(e){
    setInput(e.target.value);
  }

  const addTodo = function(){
    if(input !== ""){
      const newTodo = {id: Math.random(), value: input}
      const newTodos = [newTodo];

      setTodos(newTodos)
    }
  }

  const removeTodo = function(id){
    const removedTodos = todos.filter(todo => {
      return todo.id !== id
    })
    setTodos(removedTodos)
  }
  return (
    <div className="App">
      <h1>Todo App</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder='Write your task here' value={input} onChange={handleChange}></input>
        <button onClick={addTodo}>Add Todo</button>
      </form>

    {/*Todo List*/}

    <div>
      {todos.map((todo, id) => {
        return <div key={todo.id} style={{display: 'flex'}}>
          <div>{todo.value}</div>
          <i className="bi bi-trash3" onClick={() => removeTodo(todo.id)}></i>
        </div>
      })}
    </div>
    </div>
  );
}

export default App;
